import { Component } from '@angular/core';
import { error } from 'console';
import { Product } from '../../models/Product';
import { Purchase } from '../../models/Purchase';
import { Sale } from '../../models/Sale';
import { ProductService } from '../../services/product/product.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {

  model: Product;
  constructor(private productService: ProductService) {
    this.model = {
      name: '',
      description: '',
      quantity: 0,
      price: 0,
      productId: 0,
      purchases: [],
      sales: []
    };

  }

  onFormSubmit() {
    console.log(this.model)
    this.productService.addProduct(this.model).subscribe({
      next: (response) => {
        console.log('product added ', response);
      },
      error: (error) => {
        console.error('error when adding product', error);
      }
    })
  }
}
<div class="container">
  <h1 class="mt-3">Add Product</h1>

  <form #form="ngForm" (ngSubmit)="onFormSubmit()">
    <div class="mt-2">
      <div class="mt-3">
        <label class="form-label">Product Name</label>
        <input type="text" id="productName" name="productName" class="form-control" [(ngModel)]="model.name"/>
      </div>
    </div>


    <div class="mt-2">
      <div class="mt-3">
        <label class="form-label">Product Description</label>
        <input type="text" id="productDescription" name="productDescription" class="form-control" [(ngModel)]="model.description"/>
      </div>
    </div>

    <div class="mt-2">
      <div class="mt-3">
        <label class="form-label">Product Quantity</label>
        <input type="number" id="quantity" name="quantity" class="form-control"  [(ngModel)]="model.quantity"/>
      </div>
    </div>

    <div class="mt-2">
      <div class="mt-3">
        <label class="form-label">Product Price</label>
        <input type="number" id="price" name="price" class="form-control" [(ngModel)]="model.price" />
      </div>
    </div>

    <div class="mt-2">


        <input type="submit" class="btn btn-success"  />

    </div>
  </form>

</div>
